	// Спиннер
	function spinner_launch() {
		var is_there_spinner = $('.start_calculator[data-id="'+calc_id+'"] .calc_spinner').length;
		if (is_there_spinner) {
			$('.start_calculator[data-id="'+calc_id+'"] .calc_spinner').each(function() {
				var calc_spinner = $(this);
				$(this).find('.spinner').spinner({
					min: calc_spinner.data('min'),
					max: calc_spinner.data('max'),
					spin: function(event, ui) {
						if (ui.value < 0) ui.value = 0;
						calc_spinner.attr('data-quantity',''+ui.value+'');
						calc_spinner.find('input').val(ui.value);
						calc_row();
					}
				});

				calc_spinner.find('input').keydown(function(e) {
					if (e.keyCode != 8 && !/^[0-9]+$/g.test(e.key)) return false;
					else {
						let max = parseInt(calc_spinner.data('max'));
						let newVal = parseInt($(this).val() + '' + e.key);
						if (newVal <= max) {
							calc_spinner.attr('data-quantity', newVal);
							calc_row();
						} else {
							$(this).val(max);
							calc_spinner.attr('data-quantity', max);
							calc_row();
							return false;
						}
						return true;
					}
				});
			});
		};
	};

