	function getRowValue(element) {
		let result = false;

		let val;
		switch (element.data('type')) {
			case 'TYPE1':
				val = element.find('input').val();
				if (val > 0) {
					result = val;
					result += ' ' + element.find('.calc_slider_field_label').text();
				}
			case 'TYPE2':
				val = element.find('.spinner-slider-val').val();
				if (val > 0) result = val;
				break;
			case 'TYPE3':
				result = element.find('.calc_radio_button.checked input').val();
				break;
			case 'TYPE4':
				result = element.find('.calc_radio.checked input').val();
				break;
			case 'TYPE5':
				result = element.find('select').val();
				break;
			case 'TYPE6':
				element.find('.calc_checkboxes .checked').each(function(idx, el) {
					if (idx == 0) result = $(el).find('input').val();
					else result += ', ' + $(el).find('input').val();
				});
				break;
			case 'TYPE7':
				element.find('.calc_customselect_output .checked').each(function(idx, el) {
					if (idx == 0) result = $(el).data('title');
					else result += ', ' + $(el).data('title');
				});
				break;
			case 'TYPE8':
				if (element.find('.calc_checkbox').hasClass('checked')) result = stringYes;
				break;
			case 'TYPE9':
				result = element.find('.calc_radio.checked input').val();
				break;
			case 'TYPE10':
				element.find('.calc_checkboxes .checked').each(function (idx, el) {
					if (idx == 0) result = $(el).find('input').val();
					else result += ', ' + $(el).find('input').val();
				});
				break;
			case 'TYPE11':
				result = element.find('.calc_switch.checked input').val();
				break;
			case 'TYPE12':
				element.find('.calc_switches .checked').each(function (idx, el) {
					if (idx == 0) result = $(el).find('input').val();
					else result += ', ' + $(el).find('input').val();
				});
				break;
			case 'TYPE13':
				if (element.find('.calc_switch').hasClass('checked')) result = stringYes;
				break;
			case 'TYPE15':
				val = element.find('.spinner-slider-val').val();
				if (val > 0) result = val;
				break;
		}

		return result;
	}

	// Генерим и отправляем текст
	function calc_result(calcElement) {
		let alert = false;
		let alertField = '';
		const result = {
			rows: [],
			total: 0
		};

		calcElement.find('.calc_row').each(function(index, element) {
			element = $(element);
			if (element.css('display') == 'block') {
				const arr = {
					type: element.data('type'),
					name: element.find('.calc_elem_name').text(),
					total: element.find('.calc_row_price span').text(),
					value: getRowValue(element)
				};

				if (element.data('type') == 'TYPE8' || element.data('type') == 'TYPE13') {
					arr.name = element.find('input').val();
				}

				if (arr.value) {
					result.rows.push(arr);
				} else {
					if (element.data('required') == 'Y') {
						alert = true;
						alertField = arr.name;
					}
				}
			}
		});

		if (alert) {
			$('.start_calculator[data-id="'+calc_id+'"] .calc_popup_alert .calc_popup_errors span').text(alertField);
			$('.start_calculator[data-id="'+calc_id+'"] .calc_popup_back, .calc_popup_alert').fadeIn(300);
		} else {
			result.total = calcElement.find('input[name="calc_totals"]').val();

			let formdata = new FormData();
			formdata.append('calc_data', JSON.stringify(result));
			formdata.append('calc_id', calc_id);
			if(calcElement.find('input[name="calc_user_name"]').val()) formdata.append('calc_user_name', calcElement.find('input[name="calc_user_name"]').val());
			if(calcElement.find('input[name="calc_user_phone"]').val()) formdata.append('calc_user_phone', calcElement.find('input[name="calc_user_phone"]').val());
			if(calcElement.find('input[name="calc_user_mail"]').val()) formdata.append('calc_user_mail', calcElement.find('input[name="calc_user_mail"]').val());
			if(calcElement.find('textarea[name="calc_user_comment"]').val()) formdata.append('calc_user_comment', calcElement.find('textarea[name="calc_user_comment"]').val());
			
			let filesElement = calcElement.find('input[name="calc_user_file"]');
			if (filesElement.length > 0) {
				let files =  filesElement[0].files;
				if (files.length > 0) {
					for(fi = 0; fi < files.length; fi++) {
						formdata.append('calc_user_file[]', files[fi]);
					}
				}
			}

			$.ajax({
				url: document.location.href,
				type: 'POST',
				dataType: 'text',
				processData: false,
				contentType: false,
				data: formdata,
				success: function(response) { //Данные отправлены успешно
					$('.start_calculator[data-id="'+calc_id+'"] .calc_popup_back, .calc_popup_success').fadeIn(300);
					send_metrika();
				},
				error: function(response) { // Данные не отправлены
					alert('Error send data');
				}
			});
		}
	};

